import React from 'react';
import ReactDOM from 'react-dom/client';

const data = [
  {
    curso: 'Matemáticas',
    alumnos: [
      { nombre: 'Ana', nota: 18 },
      { nombre: 'Luis', nota: 15 },
      { nombre: 'Carlos', nota: 20 },
    ],
  },
  {
    curso: 'Historia',
    alumnos: [
      { nombre: 'Ana', nota: 16 },
      { nombre: 'Luis', nota: 13 },
      { nombre: 'Carlos', nota: 17 },
    ],
  },
];

const calcularPromedio = (alumnos) =>
  (alumnos.reduce((acc, a) => acc + a.nota, 0) / alumnos.length).toFixed(2);

const App = () => {
  return (
    <div>
      <h1>Análisis de rendimiento académico</h1>
      {data.map(({ curso, alumnos }) => (
        <div key={curso} style={{ marginBottom: '20px' }}>
          <h2>{curso}</h2>
          <p>Promedio: {calcularPromedio(alumnos)}</p>
          <ul>
            {alumnos.map(({ nombre, nota }) => (
              <li key={nombre}>
                {nombre}: {nota}
              </li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
};

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(<App />);
